--Exercicio 1:select NomeEmpregado, DataAdmissao, 	datediff(MONTH, DataAdmissao, '2000-12-31') as MesesTrabalhoAte2000				from Empregado					where DataAdmissao between '1980-05-01' and '1982-01-20'--Exercicio 2: select top(1) Cargo, 	  count(NomeEmpregado) as QuantidadeEmpregados		from empregado 			group by cargo				order by QuantidadeEmpregados desc--Exercicio 3: select UF, 	count(nome) as QuantidadeCidades		from Cidade 			group by UF--Exercicio 4: select Nome, UF,	count(1) as Duplicada		from Cidade group by Nome, UF			having count(Nome) > 1 		--Exercicio 5: select max(IDAssociado+1) as ProximoID	from Associado --Exercicio 6: select NomeEmpregado, Salario,	case when Salario <= 1164 then 0		when Salario <= 2326 then 15 			else 27.5 				End Salario					from Empregado